sqlMap.select:
SELECT X.RN, X.TOT_CNT, X.CART_SN, X.GOODS_CODE, FN_PURCHS_AMOUNT(X.ESNTL_ID, X.CART_SN) AS PURCHS_AMOUNT, X.TOUR_DE, X.GOODS_NM, X.FILE_CODE, X.WRITNG_ID, X.WRITNG_DE FROM ( SELECT A.*, ROWNUM AS RN, COUNT(*) OVER() AS TOT_CNT FROM (
SELECT CG.ESNTL_ID, CG.CART_SN, CG.GOODS_CODE, CG.TOUR_DE, G.GOODS_NM, G.FILE_CODE, CG.WRITNG_ID, CG.WRITNG_DE FROM TB_CART_GOODS CG INNER JOIN TB_GOODS G ON G.GOODS_CODE = CG.GOODS_CODE AND G.DELETE_AT = 'N' WHERE CG.ESNTL_ID = #esntl_id# AND CG.EXPRTN_AT = 'N' ORDER BY CG.WRITNG_DE DESC
) A ) X WHERE (RN > #startRow# AND RN <= #endRow#) ORDER BY RN ASC

sqlMap.delete:
BEGIN

DELETE FROM TB_CART_GOODS
WHERE ESNTL_ID = #esntl_id#	AND CART_SN = #cart_sn#;

DELETE FROM TB_CART_NMPR
WHERE ESNTL_ID = #esntl_id#	AND CART_SN = #cart_sn#;

END;
